var submitFrom = function(formid, reload, modalid) {
	$form = $(formid);
	if (!$form) return;
	$.ajax({
		url: $form.attr('action'),
		type: $form.attr('method'),
		data: $form.serialize(),
		success: function(data) {
			console.log(data);
			if (data && data.s == 1) {
				$(modalid).modal('hide');
				if (reload) {
					window.location.reload();
				}
			} else {
				//TODO $form.find('.modal-message');
				alert('操作失败');
			}

		},
		error: function(data) {
			console.log(data)
			alert('操作失败');
		}
	});
};

